                                                $('#oos_customer_email').val('');
                                        }

                                        function oosHookJsCodeMailAlert() {
                                            $.ajax({
                                                type: 'POST',
                                                url: "https://www.joyalestore.com/module/mailalerts/actions?process=check",
                                                data: 'id_product=1887&id_product_attribute=' + $('#idCombination').val(),
                                                success: function(msg) {
                                                    if ($.trim(msg) == '0') {
                                                        $('#mailalert_link').show();
                                                        $('#oos_customer_email').show();
                                                        $('#oosHook').find('#gdpr_consent').show();
                                                    } else {
                                                        $('#mailalert_link').hide();
                                                        $('#oos_customer_email').hide();
                                                        $('#oosHook').find('#gdpr_consent').hide();
                                                    }
                                                }
                                            });
                                        }

                                        function addNotification() {
                                            $.ajax({
                                                type: 'POST',
                                                url: "https://www.joyalestore.com/module/mailalerts/actions?process=add",
                                                data: 'id_product=1887&id_product_attribute=' + $('#idCombination').val() + '&customer_email=' + $('#oos_customer_email').val() + '',
                                                success: function(msg) {
                                                    if ($.trim(msg) == '1') {
                                                        $('#mailalert_link').hide();
                                                        $('#oos_customer_email').hide();
                                                        $('#oosHook').find('#gdpr_consent').hide();
                                                        $('#oos_customer_email_result').html("Demande de notification enregistrée");
                                                        $('#oos_customer_email_result').css('color', 'green').show();
                                                    } else if ($.trim(msg) == '2') {
                                                        $('#oos_customer_email_result').html("Vous avez déjà une alerte pour ce produit");
                                                        $('#oos_customer_email_result').css('color', 'red').show();
                                                    } else {
                                                        $('#oos_customer_email_result').html("Votre adresse e-mail est invalide");
                                                        $('#oos_customer_email_result').css('color', 'red').show();
                                                    }
                                                }
                                            });
                                            return false;
                                        }

                                        $(document).ready(function() {
                                            oosHookJsCodeMailAlert();
                                            $('#oos_customer_email').bind('keypress', function(e) {
                                                if (e.keyCode == 13) {
                                                    addNotification();
                                                    return false;
                                                }
                                            });
                                        });

                                        //]]>
                                    </script>

                                    <!-- MODULE MailAlerts -->
                                    <form>
                                        <input type="text" id="oos_customer_email" name="customer_email" size="20" value="votre@email.com" class="mailalerts_oos_email" onclick="clearText();"><br>


                                        <button type="submit" class="btn btn-default" title="Prévenez-moi lorsque le produit est disponible" onclick="return addNotification();" id="mailalert_link" rel="nofollow">Prévenez-moi lorsque le produit est disponible</button>
                                        <span id="oos_customer_email_result" style="display:none;"></span>
                                    </form>
                                    <!-- END : MODULE MailAlerts -->

                                </div>










                            </div>




